    private HubConnection hubConnection;
    private List<string> messages = new List<string>();
        
    private string userInput;
    private string messageInput;
    private string name;
    private string someoneTyping;

    [Parameter]
    public string fixName { get; set; }

    protected override async Task OnInitializedAsync()
    {
        hubConnection = new HubConnectionBuilder()
            .WithUrl(NavigationManager.ToAbsoluteUri("/ChatHub"))
            .Build();

       hubConnection.On<string, string>("ReceivingMessageFromAuser", (userInfoFromTheServer, messageFromTheuser) =>
        {
            var encodedMsg = $"{userInfoFromTheServer}: {messageFromTheuser}";
            var encodedMessageWIthoutTemplatedString = userInfoFromTheServer + ": " + messageFromTheuser;
            messages.Add(encodedMsg);
            StateHasChanged();
        });
        hubConnection.On<string>("ReceiveSomeoneTyping", (user) =>
        {
            if (string.IsNullOrEmpty(user)) someoneTyping = "Someone is typing ...";
            else someoneTyping = $"{user} is typing ...";
            StateHasChanged();
        });



        await hubConnection.StartAsync();
    }
     protected override async Task OnParametersSetAsync()
    {
        await base.OnParametersSetAsync();
        name = fixName ?? string.Empty;
    }
    async Task SendIAmTypingExceptMe() =>
        await hubConnection.SendAsync("SendSomeoneTyping", name);
    async Task Send() =>
        await hubConnection.SendAsync("SendMessage", userInput, messageInput);

        async Task SendToMyself() =>
    await hubConnection.SendAsync("SendMessageToCaller", userInput, messageInput);

    public bool IsConnected =>
        hubConnection.State == HubConnectionState.Connected;

    public bool shouldDisabledNameInput =>
         string.IsNullOrEmpty(fixName) ? false : true;

    public async ValueTask DisposeAsync()
    {
        if (hubConnection is not null)
        {
            await hubConnection.DisposeAsync();
        }
    }
    

